import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { BrowserMultiFormatReader } from '@zxing/library';
import axios from 'axios';
import { Button, Container, Row, Col, Card, Alert, Spinner, ToggleButton } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/App.css';

function Scanner() {
    const { eventId } = useParams();
    const [decodedInfo, setDecodedInfo] = useState(null);
    const [documentMatch, setDocumentMatch] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [userInfo, setUserInfo] = useState({ nombre: '', rol: '', color: '' });
    const [eventName, setEventName] = useState('');
    const [isCameraVisible, setIsCameraVisible] = useState(true);
    const [isProcessing, setIsProcessing] = useState(false);
    const [isEntry, setIsEntry] = useState(true);

    const isRequestInProgress = useRef(false);

    useEffect(() => {
        const fetchEventName = async () => {
            try {
                const response = await axios.get(`/api/events/${eventId}`);
                setEventName(response.data.nombre);
            } catch (error) {
                setErrorMessage('No se pudo cargar el nombre del evento.');
            }
        };

        fetchEventName();
    }, [eventId]);

    const handleToggle = () => {
        setIsEntry((prev) => !prev);
    };

    return (
        <Container fluid className="app-container">
            <Row className="justify-content-center text-center">
                <Col xs={12} className="mb-3">
                    <h2 className="app-title">
                        {eventName ? `Evento: ${eventName}` : <Spinner animation="border" />}
                    </h2>
                </Col>
                <Col xs={12} className="mb-2">
                    <ToggleButton
                        type="checkbox"
                        variant={isEntry ? 'primary' : 'danger'}
                        checked={isEntry}
                        onChange={handleToggle}
                    >
                        {isEntry ? 'Entrada' : 'Salida'}
                    </ToggleButton>
                </Col>
            </Row>
        </Container>
    );
}

export default Scanner;
