import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Spinner, Alert, Container, Form, Row, Col, Button } from 'react-bootstrap';

const Logs = ({ eventId }) => {
    const [logs, setLogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [filters, setFilters] = useState({
        date: '',
        estado: '',
        usuario: '',
    });

    // FunciÃ³n para formatear la fecha
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleString('es-ES', {
            year: 'numeric',
            month: 'short',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const fetchLogs = () => {
        setLoading(true);
        setError('');

        let queryParams = [];
        if (filters.date) queryParams.push(`date=${filters.date}`);
        if (filters.estado) queryParams.push(`estado=${filters.estado}`);
        if (filters.usuario) queryParams.push(`usuario=${filters.usuario}`);

        const queryString = queryParams.length ? `?${queryParams.join('&')}` : '';

        axios.get(`http://localhost:5000/api/events/${eventId}/logs${queryString}`)
            .then(response => {
                if (response.data.length === 0) {
                    setError('No se encontraron registros para este evento con los filtros aplicados.');
                } else {
                    setLogs(response.data);
                }
            })
            .catch(err => {
                console.error('Error al obtener los registros:', err);
                setError('Error al obtener los registros');
            })
            .finally(() => setLoading(false));
    };

    useEffect(() => {
        if (eventId) {
            fetchLogs();
        } else {
            setError('Evento no vÃ¡lido');
            setLoading(false);
        }
    }, [eventId]);

    return (
        <Container className="mt-3">
            <h2 className="text-center">ðŸ“œ Registros</h2>

            {/* Filtros */}
            <Form className="mb-3">
                <Row>
                    <Col md={4}>
                        <Form.Group>
                            <Form.Label>Fecha</Form.Label>
                            <Form.Control
                                type="date"
                                value={filters.date}
                                onChange={(e) => setFilters({ ...filters, date: e.target.value })}
                            />
                        </Form.Group>
                    </Col>
                    <Col md={4}>
                        <Form.Group>
                            <Form.Label>Estado</Form.Label>
                            <Form.Select
                                value={filters.estado}
                                onChange={(e) => setFilters({ ...filters, estado: e.target.value })}
                            >
                                <option value="">Todos</option>
                                <option value="1">âœ… Ingreso</option>
                                <option value="0">ðŸšª Egreso</option>
                            </Form.Select>
                        </Form.Group>
                    </Col>
                    <Col md={4}>
                        <Form.Group>
                            <Form.Label>Usuario</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Nombre del usuario"
                                value={filters.usuario}
                                onChange={(e) => setFilters({ ...filters, usuario: e.target.value })}
                            />
                        </Form.Group>
                    </Col>
                </Row>
                <Button className="mt-2" onClick={fetchLogs}>Filtrar</Button>
            </Form>

            {loading && <Spinner animation="border" className="d-block mx-auto" />}

            {error && <Alert variant="danger" className="text-center">{error}</Alert>}

            {!loading && logs.length > 0 && (
                <Table striped bordered hover responsive className="mt-3">
                    <thead className="table-dark">
                        <tr>
                            <th>#</th>
                            <th>Fecha</th>
                            <th>Estado</th>
                            <th>Usuario</th>
                        </tr>
                    </thead>
                    <tbody>
                        {logs.map((log, index) => (
                            <tr key={log.id_logs}>
                                <td>{index + 1}</td>
                                <td>{formatDate(log.fecha)}</td>
                                <td>{log.estado === 1 ? "âœ… Ingreso" : "ðŸšª Egreso"}</td>
                                <td>{log.usuario}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}

            {!loading && logs.length === 0 && (
                <Alert variant="warning" className="text-center">No hay registros aÃºn.</Alert>
            )}
        </Container>
    );
};

export default Logs;
